if(scenario.symptomsInfo.length > 0){
    var historicIsExposed = 0;
    var numberOfDays = (scenario.symptomsInfo.length > scenario.quarantineDays) ? scenario.quarantineDays : scenario.symptomsInfo.length;
    for(var i=0; i < numberOfDays; i++){
        var historyExposureDate = scenario.symptomsInfo[i].ExposureDate;
        if(historicIsExposed == 0){
            historicIsExposed = scenario.symptomsInfo[i].UserIsExposed;            
        }
    }
}

if(historicIsExposed){
    scenario.isExposed = 1;
}

//Logic for Lab test info: Get ONLY the most recent TestDate and TestResult from LabTestInfo for this UserId
if(scenario.labTestInfo.length > 0){
    scenario.lastLabTestRecord =  scenario.labTestInfo[scenario.labTestInfo.length - 1];
